void destroy(SDL_Window *pWindow,SDL_Surface *surface){
/**
* @brief Fonction de destruction de surface et de la fenêtre
*/
    SDL_FreeSurface(surface);
    SDL_DestroyWindow(pWindow);
    SDL_Quit();
    exit(1);
}

int initSDL() {
/**
* @brief Initialisation de la bibliothèque SDL et création de la fenêtre
*/

    if (SDL_Init(SDL_INIT_VIDEO) != 0) {
        fprintf(stderr, "SDL_Init Error: %s\n", SDL_GetError());
        return 1;
    }

    window = SDL_CreateWindow("Tiled Map", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, HEIGHT,WIDTH, SDL_WINDOW_SHOWN);
    if (window == NULL) {
        fprintf(stderr, "SDL_CreateWindow Error: %s\n", SDL_GetError());
        SDL_Quit();
        return 1;
    }


    return 0;
}
int printLayer(SDL_Surface * screen,int tiles[4160],char * nom_fichier,int firstgid, int tile_width,int tile_height){
/**
* @brief affiche la map couche par couche
*/
    
    SDL_Surface * tile = IMG_Load(nom_fichier);
    SDL_Rect rect = {0,0,tile_width,tile_height};
    int tile_x;
    int tile_y;
    SDL_Rect src_rect;
    src_rect.h=16;
    src_rect.w=16;
    for(int i = 0 ;i < 52 ; i++){
        for(int j = 0 ;j < 80 ; j++){
            if(tiles[i*80+j]==0){
                continue;
            }
            tile_x = ((tiles[i*80+j]-1)/tile_height);
            tile_y = ((tiles[i*80+j]-1)%tile_width);
            src_rect.x = tile_y * tile_height;
            src_rect.y = tile_x * tile_width;
            rect.x = tile_width*j;
            rect.y = tile_height*i;
            SDL_BlitSurface(tile,&src_rect,screen,&rect);
        }
    }
    SDL_FreeSurface(tile);
    SDL_UpdateWindowSurface(window);
    
}

int localisationValide(int x,int y){
/**
* @brief Compare la position du joueur avec le table des collisions
*/
    if(x<0||x>HEIGHT-32||y<0||y>WIDTH-32)
        return 0;
    if(collision[(y/16)*80+(x/16)])
        return 0;
    return 1;
}

void printSpirit(SDL_Surface * screen,char *nom_fichier,int x,int y){
/**
* @brief Affiche le joueur sur la map
*/ 
    SDL_Surface * spirit = IMG_Load(nom_fichier);
    printf("image good");
    int i=0;
    SDL_Rect rect = {x,y,32,32};
    SDL_Rect copy_rect = {0,0,32,32};
    
    enum directions dir = DOWN;
    enum actions act = DEF1;
// prq 32 et pas 16 pcq on veut recuperer les 4 part du personnage
    SDL_Rect src_rect = {dir*16,act*16,32,32};
    SDL_Surface* copy = SDL_CreateRGBSurface(0, 32, 32, screen->format->BitsPerPixel, 0, 0, 0, 0);
    if(SDL_BlitSurface(screen,&rect,copy,&copy_rect)<0)
        printf("erreur 1\n");
    if(SDL_BlitSurface(spirit,&src_rect,screen,&rect)<0)
        printf("erreur 2\n");
    SDL_UpdateWindowSurface(window);
    int running = 1;
    Uint32 start_time = SDL_GetTicks();
    Uint32 elapsed_time;
    Uint32 frame_time = 100; // 100 milliseconds per frame
    printf("goo!!!");
      while (running) {
        SDL_Event e;
        while (SDL_PollEvent( & e)) {
          switch (e.type) {
            case SDL_QUIT:
                destroy(window,screen);
                running = 0;
                break;
            case SDL_KEYDOWN:
                switch (e.key.keysym.sym){
                    case SDLK_UP:
                    printf("Up\n");
                        if(localisationValide(x,y-16)){
                            dir = UP;
                            SDL_Rect nouv_rect = {rect.x,rect.y-16,rect.h,rect.w};
                            movePlayer(spirit,screen,copy,rect,nouv_rect,UP,WALK1,&start_time);
                            rect.y = nouv_rect.y;
                            y=y-16;
                        }
                        break;
                    case SDLK_DOWN:
                        if(localisationValide(x,y+16)){
                            dir = DOWN;
                            SDL_Rect nouv_rect = {rect.x,rect.y+16,rect.h,rect.w};
                            movePlayer(spirit,screen,copy,rect,nouv_rect,DOWN,WALK1,&start_time);
                            rect.y = nouv_rect.y;
                            y=y+16;
                        }
                        break;
                    case SDLK_LEFT:
                        if(localisationValide(x-16,y)){
                            dir = LEFT;
                            SDL_Rect nouv_rect = {rect.x-16,rect.y,rect.h,rect.w};
                            movePlayer(spirit,screen,copy,rect,nouv_rect,LEFT,WALK1,&start_time);
                            rect.x = nouv_rect.x;
                            x=x-16;
                        }
                        break;
                    case SDLK_RIGHT:
                        if(localisationValide(x+16,y)){
                            dir = RIGHT;
                            SDL_Rect nouv_rect = {rect.x+16,rect.y,rect.h,rect.w};
                            movePlayer(spirit,screen,copy,rect,nouv_rect,RIGHT,WALK1,&start_time);
                            rect.x = nouv_rect.x;
                            x=x+16;
                        }
                        break;
                }
                break;
            case SDL_MOUSEMOTION:
                break;
            }    
        }
        if (SDL_GetTicks() - start_time > 2000){
                    printf("waiting for 2 seconds\n");
                    Uint32 start_temps = SDL_GetTicks();
                    movePlayer(spirit,screen,copy,rect,rect,dir,DEF1,&start_temps);
                    movePlayer(spirit,screen,copy,rect,rect,dir,DEF2,&start_temps);
        }
    }
}

